import React, { useState } from 'react';
import Axios from "axios";
import { GetDataFromApi, UpdateForm } from "../utils/session";
import { useEffect } from "react";

import "../Css/Form.css";

const mapStateToProps = ({ session }) => ({
  session,
});

export default function MusicForm({session}) {

  const [textAddValue, updateInput] = useState();
  const addNewText = () => {
    if (!textAddValue) {
      return;
    } else {
      UpdateForm(session.username, textAddValue);
    }
  };

  const [data, setData] = useState([]);
  useEffect(() => {
    GetDataFromApi(session.username)
      .then((response) => setData(response))
      .then(updateInput(data?.name));
  }, []);

  const idRet = useState(JSON.parse(localStorage.getItem('userID')));
  const [diet, setDiet] = useState('');
  let id = idRet[0];

  const login = (e) => {
	e.preventDefault();

	Axios.post('http://localhost:3001/music', {userID: id, diet: diet}).then((response) => {
  });
}

  return (  
    <div id="form-main">
        <form className="form" id="form1">
          <p className="Diet">
            <textarea className="Diet" type="textarea" onChange={e => setDiet(e.target.value)} id="Diet"  />
          </p>
          <div className="submit">
            <input type="submit" onClick={login} value="Submit" id="button-blue"/>
            <div className="ease"></div>
          </div>
        </form>
      </div>
    );
  }
