import React from "react";
import { connect } from "react-redux";
import { GetDataFromApi} from "../utils/session";
import { useState } from 'react';
import { useEffect } from "react"

const mapStateToProps = ({ session }) => ({
  session
});

function poop(name, username) {
  console.log(name)
  console.log(username)

   var url =
     "api/diet/" + username;
   return fetch(url, {
     method: "UPDATE",
     headers: {
       Accept: "application/json",
       "Content-Type": "application/json",
     },
     body: JSON.stringify({
      username: username,
      name: name,
    })
   })
     .then((response) => response.json())
     .then((responseData) => {
       return responseData;
     })
     .catch((error) => console.warn(error));
}


function App ({ session }) {

  const [textAddValue, updateInput] = useState();
  const addNewText = () =>{
    if(!textAddValue){ 
      return;
    } else{
      poop(textAddValue, session.username);
    }
  }

  const [data, setData] = useState([]);
  useEffect(() => {
    GetDataFromApi(session.username)
    .then((response) => setData(response))
    .then(updateInput(data?.name));
  }, []);

  return (
    <div className="App">
        <div className="container">
            <div className="eleContainer">
              <textarea type="textbox" className="txtAdd" defaultValue={data?.name} onChange={(e)=>updateInput(e.target.value)}/>
              <button className="addNew" onClick={addNewText}>Add to Dictionary</button>
            </div>
        </div>
    </div>
  );
}
  export default connect(
    mapStateToProps
  )(App);
