import React from "react";
import { connect } from "react-redux";
import { Redirect, Route, withRouter, useLocation } from "react-router-dom";

const mapStateToProps = ({ session: { userId } }) => ({
  loggedIn: Boolean(userId),
});

const place = ({})


const Auth = ({ loggedIn, path, component: Component }) => (

  <Route path={path} render={(props) => loggedIn ? <Redirect to={useLocation()}/> : <Component {...props} />}/>
);
const Protected = ({ loggedIn, path, component: Component }) => (
  <Route path={path} render={(props) => loggedIn ? <Component {...props} /> : <Redirect to="/login" />} />
);

export const AuthRoute = withRouter(connect(mapStateToProps)(Auth));
export const ProtectedRoute = withRouter(connect(mapStateToProps)(Protected));
