import React from "react";
import { connect } from "react-redux";
import { diet, logout } from "../actions/session";
import { useState } from 'react';

function Items({lang}) {
    console.log(lang)   

    const [menuItem, setMenuItem] = useState(items);
    const [buttons] = useState(lang ? allCategories : allCategoriesGerman);

    const filter = (button) =>{
      const filteredData = items.filter(item => lang ? (item.category ===  button) : (item.categoryTest ===  button));
      setMenuItem(filteredData)
    }


    return (
      <div className="App">
        <Button button={buttons} filter={filter} />
        <Menu menuItem={menuItem} language = {lang}/>
      </div>
    );
  }
const mapStateToProps = ({ session }) => ({
    session
  });

  const mapDispatchToProps = dispatch => ({
    logout: () => dispatch(logout())
  });

  const [textAddValue, updateInput] = useState("");
  const addNewText = () =>{
    //send form data to server.
    //use library like axios.
    if(!textAddValue){ alert('input empty'); return;}

    fetch('http://localhost:5000/sendText', {
      headers: {
      'Cache-Control': 'no-cache',
      "Content-Type": "application/json",
          'Accept': "application/json",
      },
      method: "POST",
      body: JSON.stringify({text: textAddValue})
    })
    .then(res=>res.json())
    .then(result=>console.log(result))
    .catch(err=>{console.log(err)})
  }

const Login = ({ session }) => {
  console.log(session.userId)
  console.log(session.username)

  const handleSubmit = e => {
    e.preventDefault();
    const user = {
        dietry: e.target[0].value
    };
    const dietry = {
        dietry: e.target[0].value
      };
    diet(user, dietry);
  }


  return (
    <div className="App">
        <div className="container">
            <div className="eleContainer">
              <input type="text" className="txtAdd" placeholder="Type New Word" value={textAddValue} onChange={(e)=>updateInput(e.target.value)}/>
            <button className="addNew" onClick={addNewText}>Add to Dictionary</button>
            <br />
            <br />
            <input type="text" className="searchWords" placeholder="Search here"/>
            <br />
            <br />
            <ol className="vocabularyList">
              <li>words</li>
            </ol>
            </div>
        </div>
    </div>
  );
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Login);
